INSERT INTO lab_activity (id, lab_product_id, title, slug, description, oa_ids, bloom_level, target_cycle, duration_minutes, group_size, difficulty_level, steps_markdown, assessment_markdown, status) VALUES ('a1eebc99-9c0b-4ef8-bb6d-6bb9bd380a12', 'a0eebc99-9c0b-4ef8-bb6d-6bb9bd380a11', 'Test Activity', 'test-activity', 'This is a test activity for development.', ARRAY['OA_TEST_01'], 'aplicar', '1B', 30, 4, 2, '# Pasos de la Actividad\n\n1. Reúne los materiales.\n2. Sigue las instrucciones.\n3. Completa la tarea.', '# Evaluación\n\n- ¿Se completó la tarea?\n- ¿Se siguieron las instrucciones?', 'active') ON CONFLICT (slug) DO UPDATE SET title = EXCLUDED.title, description = EXCLUDED.description, status = EXCLUDED.status, updated_at = NOW();